<script src="/js/jquery-3.2.1.min.js"></script>
<script>
    $(function(){
        var uri = 'http://115.126.100.43:8082/';
        var left = ($(window).width() - $('.login').width()) / 2;
        var top = ($(window).height() - $('.login').height()) / 2 - 20;
        $('.login').offset({top:top,left:left});

        /**
         * 登录
         */
        $('#login-link').click(function(){
            $('.login').show();
            $('#bg').show();

        });

        $('#reg-link').click(function() {
            $('.login').show();
            $('#bg').show();
            showReg();
        });

        $('.login-h').click(function () {
            showLogin();
        });
        $('.reg-h').click(function () {
            showReg();
        });
        function showLogin()
        {
            $('#login-item').show();
            $('#reg-item').hide();
            $('.navtab-link').toggleClass('now');
        }

        function showReg()
        {
            $('#login-item').hide();
            $('#reg-item').show();
            $('.navtab-link').toggleClass('now');
        }

        //写cookies

        function setCookie(name,value)
        {
            var Days = 30;
            var exp = new Date();
            exp.setTime(exp.getTime() + Days*24*60*60*1000);
            document.cookie = name + "="+ escape (value) + ";expires=" + exp.toGMTString();
        }
        //读取cookies
        function getCookie(name)
        {
            var arr,reg=new RegExp("(^| )"+name+"=([^;]*)(;|$)");

            if(arr=document.cookie.match(reg))

                return unescape(arr[2]);
            else
                return null;
        }

        //删除cookies
        function delCookie(name)
        {
            var exp = new Date();
            exp.setTime(exp.getTime() - 1);
            var cval=getCookie(name);
            if(cval!=null)
                document.cookie= name + "="+cval+";expires="+exp.toGMTString();
        }

        /*如果存在token，获取用户信息*/
        var api_token = getCookie('token');

        if (api_token != null) {
            $.ajax({
                url:'users/info',
                type:'get',
                headers: {"api-token" : api_token},
                success:function (data)
                {
                    $('#J_userInfo').hide();
                    $('#login-success').show();
                    $('#username').text(data.username);
                }
            });
        }

        /*登录*/
        $('#login-button').click(function () {
            $.ajax({
                url:'/users/login',
                type:'post',
                data:{
                    'username' : $('#login-item').find('[name=user]').val(),
                    'password' : $('#login-item').find('[name=pwd]').val()
                },
                success:function (res)
                {
                    var data = JSON.parse(res);
                    if (data.error > 0) {
                        $('.login-error').text(data.message);
                        $('.login-error').show();
                    } else {
                        document.cookie = 'token=' + data.api_token;
                        $('#J_userInfo').hide();
                        $('#login-success').show();
                        $('#username').text(data.username);
                        $('.login').hide();
                        $('#bg').hide();
                    }
                }
            });
        });

        /*退出*/
        $('#logout').click(function () {
            delCookie('token');
            $('#J_userInfo').show();
            $('#login-success').hide();
        });

        $('input').click(function(){
            $('.error-msg').hide();
        });
    });
</script>
</body>
</html>
